mapper.insert:
INSERT INTO users ( uid,username, password,email,jction)
VALUES
    ( #{uid},#{username}, #{password}, #{email}, #{jction} );

mapper.update:
update users


        username=#{username}


        email=#{email}


        password=#{password}


        jction=#{jction}


where uid=#{uid}

update users


        password=#{password}


where username=#{username} and  email=#{email}

mapper.select:
select uid,username,email,jction from users


        uid = ${uid}


        AND username =  ${username}


        AND password = ${password}


        AND email = ${email}


        AND jction = #{jction}

select * from users


        uid = ${uid}


        AND username =  ${username}


        AND password = ${password}


        AND email = ${email}


        AND jction = #{jction}
